// Code Node: Filter New Transactions & Format for Telegram

// Get the response from HTTP Request
const response = $input.first().json;

// Get workflow static data to track seen transactions
const staticData = $getWorkflowStaticData('global');

// Initialize if first run
if (!staticData.seenTxHashes) {
  staticData.seenTxHashes = [];
}

// Your wallet address to monitor
const MY_WALLET = 'YOUR_WALLET_ADDRESS'.toLowerCase();

// Extract transactions from the response
const txList = response.items || [];

// Find new transactions
const newTransactions = [];

for (const tx of txList) {
  const txHash = tx.hash;

  // Skip if we've already seen this transaction
  if (staticData.seenTxHashes.includes(txHash)) {
    continue;
  }

  // Check if transaction involves our wallet
  const fromAddress = tx.from?.hash?.toLowerCase() || '';
  const toAddress = tx.to?.hash?.toLowerCase() || '';

  if (fromAddress === MY_WALLET || toAddress === MY_WALLET) {
    newTransactions.push(tx);
    staticData.seenTxHashes.push(txHash);
  }
}

// Keep only last 500 hashes to prevent memory bloat
if (staticData.seenTxHashes.length > 500) {
  staticData.seenTxHashes = staticData.seenTxHashes.slice(-500);
}

// If no new transactions, return flag to skip Telegram
if (newTransactions.length === 0) {
  return [{
    json: {
      hasNew: false
    }
  }];
}

// Format each new transaction for Telegram
return newTransactions.map(tx => {
  const fromAddress = tx.from?.hash || 'Unknown';
  const toAddress = tx.to?.hash || 'Unknown';
  const isIncoming = toAddress.toLowerCase() === MY_WALLET;

  // Convert value from Wei to COTI (18 decimals)
  const valueWei = BigInt(tx.value || '0');
  const valueCoti = Number(valueWei) / 1e18;

  // Format timestamp
  const timestamp = new Date(tx.timestamp).toLocaleString('en-US', {
    dateStyle: 'medium',
    timeStyle: 'short'
  });

  // Calculate fee in COTI
  const feeWei = BigInt(tx.fee?.value || '0');
  const feeCoti = Number(feeWei) / 1e18;

  // Build Telegram message
  const emoji = isIncoming ? 'üì•' : 'üì§';
  const direction = isIncoming ? 'INCOMING' : 'OUTGOING';
  const statusEmoji = tx.status === 'ok' ? '‚úÖ' : '‚ùå';

  const message = `${emoji} *${direction} TRANSACTION* ${statusEmoji}

üí∞ *Amount:* \`${valueCoti.toFixed(4)} COTI\`
üì§ *From:* \`${fromAddress.slice(0, 8)}...${fromAddress.slice(-6)}\`
üì• *To:* \`${toAddress.slice(0, 8)}...${toAddress.slice(-6)}\`
üß± *Block:* \`${tx.block_number}\`
‚õΩ *Fee:* \`${feeCoti.toFixed(6)} COTI\`
‚è∞ *Time:* ${timestamp}

üîó [View on CotiScan](https://explorer.coti.io/tx/${tx.hash})`;

  return {
    json: {
      hasNew: true,
      message: message,
      txHash: tx.hash,
      isIncoming: isIncoming,
      amount: valueCoti,
      status: tx.status
    }
  };
});
